import { useEffect, useState } from 'react';
import './App.css'; 
import Navbar from './components/Navbar';
import Textbox from './components/Textbox';
import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes
} from 'react-router-dom';
import About from './components/About';
import Home from './components/Home';
import WeatherWidget from './components/WeatherWidget';
import Search from './components/Search';
import axios from 'axios';

function App() {

  const [Mode, setMode] = useState('light');

  const changeMode = ()=>{

    if (Mode === 'dark'){

      setMode('light');
      document.body.style.backgroundColor = 'white';

    }
    else {

      setMode('dark');
      document.body.style.backgroundColor = '#343a40';
    }
  }

  const [value , setvalue] = useState('');
  const [weatherData, setWeatherData] = useState(null);

  function handleSearchSubmit() {
    if (value.trim() === ''){

      alert('plz insert some text');
      return;
  }
  else{
    axios.get(`/weather_data?search=${value}`).then((resp)=>{
      setWeatherData(resp.data)
    })
  }
  }

  return (
    <>
    <Router>
    <Navbar title="Weather Forecast" changemode = {changeMode} mode = {Mode}/>
    <Search mode={Mode} setvalue={setvalue} value={value} handleSearchSubmit={handleSearchSubmit}/>
    <div className="container">
    <Routes>
      <Route path='/' element={<Home/>}></Route>
          <Route path='/textbox' element={<Textbox mode={Mode} />}>
          </Route>
          <Route path='/about' element={<About mode={Mode}/>}></Route>
          <Route path='/weather_api' element={<WeatherWidget mode={Mode} country={value} weatherData={weatherData} setWeatherData={setWeatherData}/>}></Route>
      </Routes>
    </div>
    </Router>
    </>
  );
}

export default App;
