import { useState } from 'react';
import './App.css'; 
import Navbar from './components/Navbar';
import Textbox from './components/Textbox';
import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Link
} from 'react-router-dom';
import About from './components/About';

function App() {

  const [Mode, setMode] = useState('light');

  const changeMode = ()=>{

    if (Mode === 'dark'){

      setMode('light');
      document.body.style.backgroundColor = 'white';

    }
    else {

      setMode('dark');
      document.body.style.backgroundColor = '#343a40';
    }
  }

  const changeModegreen = ()=>{

    if (Mode === 'dark'){

      setMode('light');
      document.body.style.backgroundColor = 'white';

    }
    else {

      setMode('dark');
      document.body.style.backgroundColor = '#003311';
    }
  }

  const changeModered = ()=>{

    if (Mode === 'dark'){

      setMode('light');
      document.body.style.backgroundColor = 'white';

    }
    else {

      setMode('dark');
      document.body.style.backgroundColor = '#b30000';
    }
  }

  return (
    <>
    <Router>
    <Navbar title="Weather Forecast" changemode = {changeMode} changemodegreen = {changeModegreen} changemodered = {changeModered}  mode = {Mode}/>
    <div className="container">
    <Routes>
          <Route path='/' element={<Textbox mode={Mode} />}>
          </Route>
          <Route path='/about' element={<About/>}></Route>
      </Routes>
      </div>
      </Router>
      </>
  );
}

export default App;
