import { useState } from 'react';
import './App.css'; 
import Navbar from './components/Navbar';
import Textbox from './components/Textbox';
import React from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


function App() {

  const [Mode, setMode] = useState('light');

  const changeMode = ()=>{

    if (Mode === 'dark'){

      setMode('light');
      document.body.style.backgroundColor = 'white';

    }
    else {

      setMode('dark');
      document.body.style.backgroundColor = '#343a40';
    }
  }

  const changeModegreen = ()=>{

    if (Mode === 'dark'){

      setMode('light');
      document.body.style.backgroundColor = 'white';

    }
    else {

      setMode('dark');
      document.body.style.backgroundColor = '#003311';
    }
  }

  const changeModered = ()=>{

    if (Mode === 'dark'){

      setMode('light');
      document.body.style.backgroundColor = 'white';

    }
    else {

      setMode('dark');
      document.body.style.backgroundColor = '#b30000';
    }
  }

  return (
    <>
    <router>
    <Switch>
          {/* <Route path="/about">
            <About />
          </Route> */}
          <Route path="/">
          <Navbar title="Weather Forecast" changemode = {changeMode} changemodegreen = {changeModegreen} changemodered = {changeModered}  mode = {Mode}/>
          </Route>
        </Switch>
      </router>
      <Textbox mode={Mode} />
    </>
  );
}

export default App;
