import { useState } from 'react';
import './App.css'; 
import Navbar from './components/Navbar';
import Textbox from './components/Textbox';
import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes
} from 'react-router-dom';
import About from './components/About';
import Home from './components/Home';
import WeatherWidget from './components/WeatherWidget';
import Search from './components/Search';
import axios from 'axios';

function App() {

  const [Mode, setMode] = useState('light');

  const changeMode = ()=>{

    if (Mode === 'dark'){

      setMode('light');
      document.body.style.backgroundColor = 'white';

    }
    else {

      setMode('dark');
      document.body.style.backgroundColor = '#343a40';
    }
  }

  const [weatherData, setWeatherData] = useState(null);

  const handleSearch = (value) => {
    // Fetch weather data from the Flask backend
    axios.get(`/weather_data?q=${value}`)
      .then(response => {
        setWeatherData(response.data);
      })
      .catch(error => {
        console.error('Error fetching weather data:', error);
      });
  };

  return (
    <>
    <Router>
    <Navbar title="Weather Forecast" changemode = {changeMode} mode = {Mode}/>
    <Search mode={Mode} onSearch={handleSearch}/>
    <div className="container">
    <Routes>
      <Route path='/' element={<Home/>}></Route>
          <Route path='/textbox' element={<Textbox mode={Mode} />}>
          </Route>
          <Route path='/about' element={<About mode={Mode}/>}></Route>
          <Route path='/weather_api' element={<WeatherWidget mode={Mode}/>}></Route>
      </Routes>
    </div>
    </Router>
    </>
  );
}

export default App;
