import React, { useEffect, useState } from 'react'
import axios from 'axios';
import setWeatherData from './WeatherWidget';


export default function search(props) {

    const [value , setvalue] = useState('');

    const send_data = () =>{
        useEffect(() => {
            let city = setvalue;
            // Fetch weather data from the Flask backend
            axios.get(`/weather_data/${city}`)
              .then(response => {
                setWeatherData(response.data);
              })
              .catch(error => {
                console.error('Error fetching weather data:', error);
              });
          }, []);

    }
    const on_change = (event) =>{

        setvalue(event.target.value)
    }
    return (
        <>
            <div className="container">
                <div className="input-group">
                    <input type="search" onChange={on_change} value={value} className="form-control rounded" style={{
                        color: props.mode === 'dark' ? 'white' : 'black',
                        backgroundColor: props.mode === 'dark' ? '#212529' : 'white'
                    }} placeholder="Search" aria-label="Search" aria-describedby="search-addon" />
                    <button type="button" className="btn btn-outline-primary" onClick={send_data}>Search</button>
                </div>
            </div>

        </>
    )
}
