import axios from 'axios';
import React, { useState } from 'react';

export default function Search(props,{onSearch}) {

    const [value , setvalue] = useState('');

    const handleSearch = () => {
        onSearch = value;
    }
    return (
        <>
            <div className="container">
                <div className="input-group">
                    <input type="search" onChange={e => setvalue(e.target.value)} value={value} className="form-control rounded" style={{
                        color: props.mode === 'dark' ? 'white' : 'black',
                        backgroundColor: props.mode === 'dark' ? '#212529' : 'white'
                    }} placeholder="Search" aria-label="Search" aria-describedby="search-addon" />
                    <button type="button" className="btn btn-outline-primary" onClick={handleSearch}>Search</button>
                </div>
            </div>

        </>
    )
}
