import { useState } from "react"
import React from "react"

export default function Textbox(props){

    const [text, newtext] = useState('Hello')

    const On_click = ()=>{
        let new_t = text.toUpperCase();
        newtext(new_t)
    }

    const On_change = (event)=>{
        newtext(event.target.value)

    }

    return(
        <>
        <div className="container">

        <div className="mb-3">
        <label htmlFor="exampleFormControlTextarea1" className="form-label">Enter any to text</label>
        <textarea className="form-control" id="exampleFormControlTextarea1" onChange={On_change} value = {text} rows="6"></textarea><br />
        <button className="btn btn-outline-success" onClick={On_click} type="submit">Upper</button>
        </div>

        </div>


        </>

    )
}
