import axios from 'axios';
import React, { useState } from 'react'
import WeatherWidget from './components/WeatherWidget'

export default function search(props) {

    const [value , setvalue] = useState('');

    const send_data = () =>{
        axios.get(`weather_data?city=${value}`).then(response => {
            WeatherWidget.setWeatherData(response.data);
        }).catch(error => {
            console.error('Error fetching data', error);
        });

    };
    return (
        <>
            <div className="container">
                <div className="input-group">
                    <input type="search" onChange={e => setvalue(e.target.value)} value={value} className="form-control rounded" style={{
                        color: props.mode === 'dark' ? 'white' : 'black',
                        backgroundColor: props.mode === 'dark' ? '#212529' : 'white'
                    }} placeholder="Search" aria-label="Search" aria-describedby="search-addon" />
                    <button type="button" className="btn btn-outline-primary" onClick={send_data}>Search</button>
                </div>
            </div>

        </>
    )
}
