import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';
import { WiDaySunny, WiCloudy, WiRain, WiSnow } from 'react-icons/wi';

export default function WeatherWidget() {
        const [weatherData, setWeatherData] = useState(null);

        useEffect(() => {
          // Fetch weather data from the Flask backend
          axios.get('/weather_data')
            .then(response => {
              setWeatherData(response.data);
            })
            .catch(error => {
              console.error('Error fetching weather data:', error);
            });
        }, []);

        const getWeatherIcon = (weatherData) => {
            console.log(weatherData)
            console.log(weatherData.weather)
            console.log(weatherData.weather.length)
            if (!weatherData || !weatherData.weather || weatherData.weather.length === 0) {
                return null;
              }

            const weatherCondition = weatherData.weather[0].main;  
            switch (weatherCondition) {
              case 'Clear':
                return <WiDaySunny />;
              case 'Clouds':
                return <WiCloudy />;
              case 'Rain':
                return <WiRain />;
              case 'Snow':
                return <WiSnow />;
              default:
                return null;
            }
          };

    return (
        <>
    <div className="container">
        <div className="weather-widget">
        {weatherData ? (
          <div>
              <div className="weather-icon">{getWeatherIcon(weatherData.main)}</div>
              <div className="weather-description">
                <h2>Weather Desciption: {weatherData.weather[0].description}</h2>
                <p>Temperature: {weatherData.main.temp} Â°C</p>
                <p>Minumum Temp:{weatherData.main.temp_min}</p>
                <p>Maximum Temp: {weatherData.main.temp_max}</p>
                <p>Feels Like: {weatherData.main.feels_like}</p>
                <p>Humidity: {weatherData.main.humidity}%</p>
                {/* Add more weather data fields as needed */}
              </div>
            </div>
        ) : (
          <p>Loading weather data...</p>
        )}
      </div>
      </div>
      </>
    )
  }
