import React from 'react';
import { useEffect } from 'react';
import axios from 'axios';
import { WiDaySunny, WiCloudy, WiRain, WiSnow, WiDust } from 'react-icons/wi';

export default function WeatherWidget(props) {
        let {weatherData, setWeatherData} = props

        if(!weatherData){
            useEffect(() => {
              // Fetch weather data from the Flask backend
              axios.get("/weather_data")
                .then(response => {
                  setWeatherData(response.data);
                })
                .catch(error => {
                  console.error('Error fetching weather data:', error);
                });
            }, []);
        }

        const getWeatherIcon = (weatherData) => {
            try {
              const weatherCondition = weatherData.weather[0].main;  
              switch (weatherCondition) {
                case 'Clear':
                  return <WiDaySunny />;
                case 'Haze':
                  return <WiCloudy />;
                case 'Clouds':
                  return <WiCloudy />;
                case 'Rain':
                  return <WiRain />;
                case 'Snow':
                  return <WiSnow />;
                case 'Dust':
                  return <WiDust />;
                default:
                  return null;
              }

            }
            catch(error){
              return (

                <div className="container">

                  <p>No data found!</p>
                </div>
              )
            }
          };

    return (
        <>
    <div className="container">
        <div className="weather-widget" style={{color:props.mode==='dark'?'white':'black',
        backgroundColor:props.mode==='dark'?'#212529':'white'}}>
        {weatherData ? (
          <div>
              <div className="weather-icon">{getWeatherIcon(weatherData)}</div>
              <div className="weather-description">
                <h3>{props.country}</h3>
                <h2>Description: {weatherData.weather[0].description}</h2>
                <p>Temperature: {weatherData.main.temp} 째C</p>
                <p>Minumum Temp:{weatherData.main.temp_min}째C</p>
                <p>Maximum Temp: {weatherData.main.temp_max}째C</p>
                <p>Feels Like: {weatherData.main.feels_like}째C</p>
                <p>Humidity: {weatherData.main.humidity}%</p>
                <p>Country: {weatherData.sys.country}</p>
                {/* Add more weather data fields as needed */}
              </div>
            </div>
        ) : (
          <p>Loading weather data...</p>
        )}
      </div>
      </div>
      </>
    )
  }
